create database if not exists `forum_db`;
use `forum_db`;
create table if not exists `user` (`id` varchar(50), `username` varchar(255), `email` varchar(255), `password` varchar(255), `avatar_path` varchar(255), `created_at` datetime(6), `updated_at` datetime(6), PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;
create table if not exists `post` (`id` varchar(50), `title` varchar(255), `content` varchar(255), `user_id` varchar(255), `created_at` datetime(6), `updated_at` datetime(6), PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;
create table if not exists `comment` (`id` varchar(50), `user_id` varchar(255), `post_id` varchar(255), `body` varchar(255), `created_at` datetime(6), `updated_at` datetime(6), PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;
create table if not exists `like` (id varchar(50), `user_id` varchar(255), `post_id` varchar(255), `created_at` datetime(6), PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=latin1;